(lambda W,s:print("Words containing 17 letters:",[w for w in W if len(w)==17],s+"containing 18+ letters:",[w for w in W if len(w)>17],"\nShortest word containing all vowels:",min([w for w in W if all([v in w for v in"aeiou"])],key=len),s+"with 4 a's:",[w for w in W if w.count("a")==4],s+"with 2+ q's:",[w for w in W if w.count("q")>=2],s+"containing cie:",[w for w in W if"cie"in w],"\nAnagrams of angle:",[w for w in W if w!="angle"and len(w)==5 and len([l for l in set(w)if l in"angle"])==5],s+"ending in iary:",len([w for w in W if w[-4:]=="iary"]),s+"with q but no u:",[w for w in W if"q"in w and not"u"in w],s+"with most e's:",[w for w in W if w.count("e")==max(W,key=lambda w:w.count("e")).count("e")]))([w.strip()for w in __import__("sys").stdin],"\nWords ")
